import React, { useState, useEffect } from "react";
import axios from "axios";

const Cards = ({ data, handleClick }) => {
  const [furniture, setFurniture] = useState([]);
  useEffect(() => {
    const getFurnitures = async () => {
      await axios
        .get("https://evening-chamber-92951.herokuapp.com/api/furniture")
        .then((res) => {
          console.log(res.data);
          setFurniture(res.data.content);
        })
        .catch((err) => {
          console.log(err);
        });
    };
    getFurnitures();
  }, []);
  const { name, price, imageUrl, countInStock, storeName } = furniture;
  return (
    <div className="cards">
      <div className="image_box">
        <img src={imageUrl} alt="" width="350px" height="350px" />
      </div>
      <div className="details">
        <p>{name}</p>
        <p>Price: ${price}</p>
        <p>Stock: {countInStock}</p>
        <p>StoreName: {storeName}</p>
        <button onClick={() => handleClick(data)}>Add to Cart</button>
      </div>
    </div>
  );
};

export default Cards;
